import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Settings.css';

function Settings() {
  const [settings, setSettings] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetchSettings();
  }, []);

  const fetchSettings = async () => {
    try {
      const response = await axios.get('/api/settings');
      setSettings(response.data);
    } catch (error) {
      console.error('Failed to fetch settings:', error);
      setMessage('Failed to load settings');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSaving(true);
    setMessage('');

    try {
      const thresholds = settings.thresholds || {};
      await axios.post('/api/settings/thresholds', {
        read_frequency_threshold: parseInt(settings.read_frequency_threshold) || 100,
        write_frequency_threshold: parseInt(settings.write_frequency_threshold) || 100,
        modification_rate_threshold: parseInt(settings.modification_rate_threshold) || 50,
        burst_intensity_multiplier: parseFloat(settings.burst_intensity_multiplier) || 3.0,
        burst_time_window_seconds: parseInt(settings.burst_time_window_seconds) || 5,
        time_window_seconds: parseInt(settings.time_window_seconds) || 1
      });
      setMessage('Settings saved successfully! Changes applied immediately.');
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage('Failed to save settings: ' + (error.response?.data?.detail || error.message));
    } finally {
      setSaving(false);
    }
  };

  const handleChange = (field, value) => {
    setSettings(prev => ({
      ...prev,
      [field]: value
    }));
  };

  if (loading) {
    return <div className="settings-loading">Loading settings...</div>;
  }

  return (
    <div className="settings-page">
      <div className="settings-container">
        <h2>Settings</h2>
        
        {message && (
          <div className={`settings-message ${message.includes('success') ? 'success' : 'error'}`}>
            {message}
          </div>
        )}

        <form onSubmit={handleSubmit} className="settings-form">
          <div className="settings-section">
            <h3>Time Window</h3>
            <div className="form-group">
              <label>Time Window (seconds)</label>
              <input
                type="number"
                min="1"
                max="60"
                value={settings.time_window_seconds || 1}
                onChange={(e) => handleChange('time_window_seconds', e.target.value)}
                required
              />
              <small>Duration of each time window for I/O aggregation</small>
            </div>
          </div>

          <div className="settings-section">
            <h3>Thresholds</h3>
            <div className="form-group">
              <label>Read Frequency Threshold</label>
              <input
                type="number"
                min="1"
                value={settings.read_frequency_threshold || settings.thresholds?.read_frequency_threshold || 100}
                onChange={(e) => handleChange('read_frequency_threshold', e.target.value)}
                required
              />
              <small>Number of reads per window to mark as high-load</small>
            </div>

            <div className="form-group">
              <label>Write Frequency Threshold</label>
              <input
                type="number"
                min="1"
                value={settings.write_frequency_threshold || settings.thresholds?.write_frequency_threshold || 100}
                onChange={(e) => handleChange('write_frequency_threshold', e.target.value)}
                required
              />
              <small>Number of writes per window to mark as high-load</small>
            </div>

            <div className="form-group">
              <label>Modification Rate Threshold</label>
              <input
                type="number"
                min="1"
                value={settings.modification_rate_threshold || settings.thresholds?.modification_rate_threshold || 50}
                onChange={(e) => handleChange('modification_rate_threshold', e.target.value)}
                required
              />
              <small>Number of modifications per window to mark as high-load</small>
            </div>
          </div>

          <div className="settings-section">
            <h3>Burst Detection</h3>
            <div className="form-group">
              <label>Burst Intensity Multiplier</label>
              <input
                type="number"
                min="1"
                step="0.1"
                value={settings.burst_intensity_multiplier || settings.thresholds?.burst_intensity_multiplier || 3.0}
                onChange={(e) => handleChange('burst_intensity_multiplier', e.target.value)}
                required
              />
              <small>Multiplier above average activity to detect bursts (e.g., 3.0 = 3x average)</small>
            </div>

            <div className="form-group">
              <label>Burst Time Window (seconds)</label>
              <input
                type="number"
                min="1"
                max="60"
                value={settings.burst_time_window_seconds || settings.thresholds?.burst_time_window_seconds || 5}
                onChange={(e) => handleChange('burst_time_window_seconds', e.target.value)}
                required
              />
              <small>Time window for burst detection analysis</small>
            </div>
          </div>

          <div className="settings-actions">
            <button type="submit" className="save-button" disabled={saving}>
              {saving ? 'Saving...' : 'Save Settings'}
            </button>
            <button type="button" className="reset-button" onClick={fetchSettings}>
              Reset
            </button>
          </div>
        </form>

        <div className="settings-section data-management">
          <h3>Data Management</h3>
          <p className="section-description">Delete historical audit logs and notifications</p>
          
          <div className="data-management-grid">
            <div className="data-group">
              <h4>Audit Logs</h4>
              <div className="delete-buttons">
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeleteData('audit', 'today')}
                >
                  Delete Today's Logs
                </button>
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeleteData('audit', 'last_hour')}
                >
                  Delete Last Hour
                </button>
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeleteData('audit', 'last_month')}
                >
                  Delete Last Month
                </button>
                <button 
                  className="delete-btn danger" 
                  onClick={() => handleDeleteData('audit', 'all')}
                >
                  Delete All Logs
                </button>
              </div>
            </div>

            <div className="data-group">
              <h4>Notifications</h4>
              <div className="delete-buttons">
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeleteData('notifications', 'today')}
                >
                  Delete Today's Notifications
                </button>
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeleteData('notifications', 'last_hour')}
                >
                  Delete Last Hour
                </button>
                <button 
                  className="delete-btn" 
                  onClick={() => handleDeleteData('notifications', 'last_month')}
                >
                  Delete Last Month
                </button>
                <button 
                  className="delete-btn danger" 
                  onClick={() => handleDeleteData('notifications', 'all')}
                >
                  Delete All Notifications
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );

  const handleDeleteData = async (type, period) => {
    if (!window.confirm(`Are you sure you want to delete ${period === 'all' ? 'all' : period.replace('_', ' ')} ${type}? This action cannot be undone.`)) {
      return;
    }

    try {
      const endpoint = type === 'audit' ? '/api/audit/logs' : '/api/notifications';
      const response = await axios.delete(endpoint, {
        params: { period }
      });
      setMessage(`Successfully deleted ${response.data.deleted_count} ${type}`);
      setTimeout(() => setMessage(''), 3000);
    } catch (error) {
      setMessage('Failed to delete: ' + (error.response?.data?.detail || error.message));
    }
  };
}

export default Settings;
