import React, { useState, useEffect } from 'react';
import './AlertBanner.css';

function AlertBanner({ alert, onDismiss }) {
  const [visible, setVisible] = useState(true);

  useEffect(() => {
    // Auto-dismiss after 10 seconds
    const timer = setTimeout(() => {
      setVisible(false);
      if (onDismiss) onDismiss();
    }, 10000);

    return () => clearTimeout(timer);
  }, [onDismiss]);

  if (!visible || !alert) return null;

  const getSeverityClass = (severity) => {
    switch (severity) {
      case 'error': return 'error';
      case 'warning': return 'warning';
      case 'info': return 'info';
      case 'success': return 'success';
      default: return 'info';
    }
  };

  return (
    <div className={`alert-banner ${getSeverityClass(alert.severity || 'warning')}`}>
      <div className="alert-content">
        <span className="alert-icon">
          {alert.severity === 'error' ? 'üî¥' : 
           alert.severity === 'warning' ? '‚ö†Ô∏è' : 
           alert.severity === 'success' ? '‚úÖ' : '‚ÑπÔ∏è'}
        </span>
        <div className="alert-text">
          <div className="alert-title">{alert.title || 'Alert'}</div>
          <div className="alert-message">{alert.message}</div>
        </div>
      </div>
      <button className="alert-close" onClick={() => {
        setVisible(false);
        if (onDismiss) onDismiss();
      }}>
        √ó
      </button>
    </div>
  );
}

export default AlertBanner;
