import React, { useState, useEffect } from 'react';
import './Login.css';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext.js';

function Login() {
  const { login, register, loading } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [needsRegister, setNeedsRegister] = useState(false);
  const [email, setEmail] = useState('');
  const [apiError, setApiError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    const res = await login(username, password);
    if (!res.success) setError(res.error || 'Login failed');
  };

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const res = await axios.get('/api/exists-users');
        if (!mounted) return;
        setNeedsRegister(!res.data.exists);
        setApiError(null);
      } catch (e) {
        // If API call fails, surface error and allow retry
        setApiError('Unable to contact backend API. Please ensure backend is running.');
        setNeedsRegister(false);
      }
    })();
    return () => { mounted = false };
  }, []);

  return (
    <div className="login-page">
      <form className="login-card" onSubmit={handleSubmit}>
        <h2>{needsRegister ? 'Create Admin Account' : 'Sign In'}</h2>
        {error && <div className="login-error">{error}</div>}
        {apiError && (
          <div className="login-error">
            {apiError} <button type="button" onClick={async () => {
              setApiError(null);
              try {
                const r = await axios.get('/api/exists-users');
                setNeedsRegister(!r.data.exists);
              } catch (err) {
                setApiError('Unable to contact backend API. Please ensure backend is running.');
              }
            }}>Retry</button>
          </div>
        )}

        <label>Username</label>
        <input value={username} onChange={e => setUsername(e.target.value)} />

        {needsRegister && (
          <>
            <label>Email</label>
            <input value={email} onChange={e => setEmail(e.target.value)} />
          </>
        )}

        <label>Password</label>
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} />

        {!needsRegister && <button type="submit" disabled={loading}>Sign In</button>}

        {needsRegister && (
          <button
            type="button"
            onClick={async () => {
              setError(null);
              const res = await register(username, email, password);
              if (!res.success) {
                setError(res.error || 'Registration failed');
              }
            }}
            disabled={loading}
          >
            Create Account
          </button>
        )}
        <div style={{marginTop:12, textAlign:'center'}}>
          {!needsRegister ? (
            <button type="button" className="link-button" onClick={() => setNeedsRegister(true)}>Need an account? Register</button>
          ) : (
            <button type="button" className="link-button" onClick={() => setNeedsRegister(false)}>Back to Sign In</button>
          )}
        </div>
      </form>
    </div>
  );
}

export default Login;
