import React, { useState, useEffect, useRef } from 'react';
import './EventLogs.css';
import axios from 'axios';

function EventLogs({ operationType, onClose }) {
  const [events, setEvents] = useState([]);
  const [filteredEvents, setFilteredEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('');
  const [pathFilter, setPathFilter] = useState('');
  const [timeFilter, setTimeFilter] = useState('all'); // all, 1m, 5m, 15m, 1h
  const [autoScroll, setAutoScroll] = useState(true);
  const [eventCounts, setEventCounts] = useState({});
  const eventsEndRef = useRef(null);
  const wsRef = useRef(null);

  const operationTypes = {
    read: { label: 'Read Operations', icon: 'üìñ', color: '#3b82f6' },
    write: { label: 'Write Operations', icon: '‚úèÔ∏è', color: '#f59e0b' },
    create: { label: 'Create Operations', icon: 'üìù', color: '#10b981' },
    delete: { label: 'Delete Operations', icon: 'üóëÔ∏è', color: '#ef4444' },
    modify: { label: 'Modifications', icon: 'üîÑ', color: '#8b5cf6' },
    all: { label: 'All Operations', icon: 'üìä', color: '#6b7280' }
  };

  const scrollToBottom = () => {
    if (autoScroll && eventsEndRef.current) {
      eventsEndRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [filteredEvents, autoScroll]);

  useEffect(() => {
    // Connect to WebSocket for real-time events
    const connectWebSocket = () => {
      const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
      const host = window.location.hostname;
      const port = process.env.REACT_APP_WS_PORT || '8001';
      const wsUrl = `${protocol}//${host}:${port}/ws`;

      wsRef.current = new WebSocket(wsUrl);

      wsRef.current.onopen = () => {
        console.log('EventLogs WebSocket connected');
      };

      wsRef.current.onmessage = (event) => {
        try {
          const data = JSON.parse(event.data);
          if (data.current_window && data.current_window.events) {
            // Process new events from current window
            const newEvents = data.current_window.events.map(evt => ({
              ...evt,
              id: `${evt.timestamp}-${evt.path}-${evt.event_type}-${Math.random()}`,
              timestamp: evt.timestamp || Date.now() / 1000
            }));
            
            if (newEvents.length > 0) {
              setEvents(prevEvents => {
                // Remove duplicates and add new events
                const existingIds = new Set(prevEvents.map(e => `${e.timestamp}-${e.path}-${e.event_type}`));
                const uniqueNewEvents = newEvents.filter(e => 
                  !existingIds.has(`${e.timestamp}-${e.path}-${e.event_type}`)
                );
                
                const combined = [...prevEvents, ...uniqueNewEvents];
                // Keep only last 1000 events for performance
                return combined.slice(-1000).sort((a, b) => b.timestamp - a.timestamp);
              });
            }
          }
        } catch (error) {
          console.error('Error parsing WebSocket message:', error);
        }
      };

      wsRef.current.onerror = (error) => {
        console.error('EventLogs WebSocket error:', error);
      };

      wsRef.current.onclose = () => {
        console.log('EventLogs WebSocket disconnected');
        // Attempt to reconnect after 3 seconds
        setTimeout(connectWebSocket, 3000);
      };
    };

    // Load initial events
    const loadInitialEvents = async () => {
      try {
        setLoading(true);
        const [historyResponse, currentResponse] = await Promise.all([
          axios.get('/api/workload/history?limit=50'),
          axios.get('/api/workload')
        ]);
        
        const allEvents = [];
        
        // Load historical events
        historyResponse.data.history.forEach(window => {
          if (window.events && window.events.length > 0) {
            window.events.forEach(evt => {
              allEvents.push({
                ...evt,
                id: `${evt.timestamp}-${evt.path}-${evt.event_type}-${Math.random()}`,
                timestamp: evt.timestamp || window.window_start
              });
            });
          }
        });
        
        // Load current window events
        if (currentResponse.data.current_window && currentResponse.data.current_window.events) {
          currentResponse.data.current_window.events.forEach(evt => {
            allEvents.push({
              ...evt,
              id: `${evt.timestamp}-${evt.path}-${evt.event_type}-${Math.random()}`,
              timestamp: evt.timestamp || currentResponse.data.current_window.window_start
            });
          });
        }
        
        setEvents(allEvents.sort((a, b) => b.timestamp - a.timestamp));
      } catch (error) {
        console.error('Failed to load initial events:', error);
      } finally {
        setLoading(false);
      }
    };

    loadInitialEvents();
    connectWebSocket();

    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
    };
  }, []);

  useEffect(() => {
    // Filter events based on operation type and filters
    let filtered = events;

    // Filter by time range
    if (timeFilter !== 'all') {
      const now = Date.now() / 1000;
      let timeLimit;
      switch (timeFilter) {
        case '1m': timeLimit = now - 60; break;
        case '5m': timeLimit = now - 300; break;
        case '15m': timeLimit = now - 900; break;
        case '1h': timeLimit = now - 3600; break;
        default: timeLimit = 0;
      }
      filtered = filtered.filter(event => event.timestamp >= timeLimit);
    }

    // Filter by operation type
    if (operationType !== 'all') {
      filtered = filtered.filter(event => {
        switch (operationType) {
          case 'read':
            // File system watchers can't detect reads, so we show creates as proxy
            return event.event_type === 'created';
          case 'write':
            // Write operations are modifications and moves
            return event.event_type === 'modified' || event.event_type === 'moved' || event.event_type === 'moved_to';
          case 'create':
            return event.event_type === 'created';
          case 'delete':
            return event.event_type === 'deleted';
          case 'modify':
            return event.event_type === 'modified';
          default:
            return true;
        }
      });
    }

    // Filter by path
    if (pathFilter) {
      filtered = filtered.filter(event => 
        event.path.toLowerCase().includes(pathFilter.toLowerCase())
      );
    }

    // Filter by general search
    if (filter) {
      filtered = filtered.filter(event => 
        event.path.toLowerCase().includes(filter.toLowerCase()) ||
        event.event_type.toLowerCase().includes(filter.toLowerCase())
      );
    }

    setFilteredEvents(filtered);

    // Calculate event counts by type for the filtered time range
    const counts = {
      created: 0,
      modified: 0,
      deleted: 0,
      moved: 0,
      total: filtered.length
    };

    filtered.forEach(event => {
      if (counts.hasOwnProperty(event.event_type)) {
        counts[event.event_type]++;
      }
    });

    setEventCounts(counts);
  }, [events, operationType, filter, pathFilter, timeFilter]);

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp * 1000).toLocaleTimeString();
  };

  const getEventIcon = (eventType) => {
    switch (eventType) {
      case 'created': return 'üìù';
      case 'modified': return 'üîÑ';
      case 'deleted': return 'üóëÔ∏è';
      case 'read': return 'üìñ';
      case 'write': return '‚úèÔ∏è';
      case 'moved': return 'üì¶';
      default: return 'üìÑ';
    }
  };

  const getEventColor = (eventType) => {
    switch (eventType) {
      case 'created': return '#10b981';
      case 'modified': return '#8b5cf6';
      case 'deleted': return '#ef4444';
      case 'read': return '#3b82f6';
      case 'write': return '#f59e0b';
      case 'moved': return '#6b7280';
      default: return '#94a3b8';
    }
  };

  const currentOp = operationTypes[operationType] || operationTypes.all;

  return (
    <div className="event-logs-modal">
      <div className="event-logs-content">
        <div className="event-logs-header">
          <div className="event-logs-title">
            <span className="event-icon" style={{ color: currentOp.color }}>
              {currentOp.icon}
            </span>
            <h3>{currentOp.label}</h3>
            <span className="event-count">({filteredEvents.length} events)</span>
          </div>
          <button className="close-btn" onClick={onClose}>‚úï</button>
        </div>

        <div className="event-logs-filters">
          <select
            value={timeFilter}
            onChange={(e) => setTimeFilter(e.target.value)}
            className="time-filter-select"
          >
            <option value="all">All Time</option>
            <option value="1m">Last 1 Minute</option>
            <option value="5m">Last 5 Minutes</option>
            <option value="15m">Last 15 Minutes</option>
            <option value="1h">Last 1 Hour</option>
          </select>
          <input
            type="text"
            placeholder="Search events..."
            value={filter}
            onChange={(e) => setFilter(e.target.value)}
            className="filter-input"
          />
          <input
            type="text"
            placeholder="Filter by path..."
            value={pathFilter}
            onChange={(e) => setPathFilter(e.target.value)}
            className="filter-input"
          />
          <label className="auto-scroll-toggle">
            <input
              type="checkbox"
              checked={autoScroll}
              onChange={(e) => setAutoScroll(e.target.checked)}
            />
            Auto-scroll
          </label>
        </div>

        <div className="event-stats">
          <div className="stat-item">
            <span className="stat-icon">üìù</span>
            <span className="stat-label">Created:</span>
            <span className="stat-value">{eventCounts.created || 0}</span>
          </div>
          <div className="stat-item">
            <span className="stat-icon">üîÑ</span>
            <span className="stat-label">Modified:</span>
            <span className="stat-value">{eventCounts.modified || 0}</span>
          </div>
          <div className="stat-item">
            <span className="stat-icon">üóëÔ∏è</span>
            <span className="stat-label">Deleted:</span>
            <span className="stat-value">{eventCounts.deleted || 0}</span>
          </div>
          <div className="stat-item">
            <span className="stat-icon">üì¶</span>
            <span className="stat-label">Moved:</span>
            <span className="stat-value">{eventCounts.moved || 0}</span>
          </div>
          <div className="stat-item total">
            <span className="stat-icon">üìä</span>
            <span className="stat-label">Total:</span>
            <span className="stat-value">{eventCounts.total || 0}</span>
          </div>
        </div>

        <div className="event-logs-list">
          {loading ? (
            <div className="loading-events">Loading events...</div>
          ) : filteredEvents.length === 0 ? (
            <div className="no-events">No events found</div>
          ) : (
            filteredEvents.map((event) => (
              <div key={event.id} className="event-item">
                <div className="event-time">
                  {formatTimestamp(event.timestamp)}
                </div>
                <div className="event-type">
                  <span 
                    className="event-type-icon"
                    style={{ color: getEventColor(event.event_type) }}
                  >
                    {getEventIcon(event.event_type)}
                  </span>
                  <span className="event-type-text">
                    {event.event_type.toUpperCase()}
                  </span>
                </div>
                <div className="event-path">
                  {event.path}
                </div>
                <div className="event-details">
                  {event.is_directory ? 'üìÅ Directory' : 'üìÑ File'}
                </div>
              </div>
            ))
          )}
          <div ref={eventsEndRef} />
        </div>
      </div>
    </div>
  );
}

export default EventLogs;
