import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './Header.css';

function Header({ isConnected, onNotificationsClick, notificationCount = 0 }) {
  const navigate = useNavigate();
  const { user, logout } = useAuth();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <header className="header">
      <div className="header-content">
        <div className="header-left">
          <h1 className="header-title" onClick={() => navigate('/dashboard')} style={{ cursor: 'pointer' }}>
            <span className="header-icon">üíæ</span>
            SAN I/O Workload Monitor
          </h1>
          <p className="header-subtitle">Real-time Storage Area Network I/O Activity Monitoring</p>
        </div>
        <div className="header-right">
          <nav className="header-nav">
            <button 
              className="nav-button" 
              onClick={() => navigate('/dashboard')}
              title="Dashboard"
            >
              üìä Dashboard
            </button>
            <button 
              className="nav-button" 
              onClick={() => navigate('/settings')}
              title="Settings"
            >
              ‚öôÔ∏è Settings
            </button>
            <button 
              className="nav-button" 
              onClick={() => navigate('/audit')}
              title="Audit Logs"
            >
              üìã Audit
            </button>
            <button 
              className="nav-button notifications-button" 
              onClick={onNotificationsClick}
              title="Notifications"
            >
              üîî Notifications
              {notificationCount > 0 && (
                <span className="notification-badge">{notificationCount}</span>
              )}
            </button>
          </nav>
          <div className={`status-indicator ${isConnected ? 'connected' : 'disconnected'}`}>
            <span className="status-dot"></span>
            <span className="status-text">
              {isConnected ? 'Connected' : 'Disconnected'}
            </span>
          </div>
          {user && (
            <div className="user-info">
              <span className="username">{user.username}</span>
              <button className="logout-button" onClick={handleLogout}>
                Logout
              </button>
            </div>
          )}
        </div>
      </div>
    </header>
  );
}

export default Header;
