import React, { useState } from 'react';
import './MetricsOverview.css';
import EventLogs from './EventLogs';

function MetricsOverview({ currentWindow, highLoadPaths }) {
  const [selectedOperation, setSelectedOperation] = useState(null);
  const { read_count, write_count, modification_count, total_events, events } = currentWindow || {};
  const { create_count, delete_count } = currentWindow || {};

  // Calculate actual operation counts from events
  const calculateOperationCounts = () => {
    if (!events || events.length === 0) {
      return {
        actualReadCount: 0,
        actualWriteCount: 0,
        actualCreateCount: 0,
        actualDeleteCount: 0,
        actualModifyCount: 0
      };
    }

    const counts = {
      actualReadCount: 0,
      actualWriteCount: 0,
      actualCreateCount: 0,
      actualDeleteCount: 0,
      actualModifyCount: 0
    };

    events.forEach(event => {
      switch (event.event_type) {
        case 'created':
          counts.actualReadCount++; // Created files are proxy for reads
          counts.actualCreateCount++;
          break;
        case 'modified':
          counts.actualWriteCount++;
          counts.actualModifyCount++;
          break;
        case 'moved':
        case 'moved_to':
          counts.actualWriteCount++;
          break;
        case 'deleted':
          counts.actualDeleteCount++;
          break;
      }
    });

    return counts;
  };

  const operationCounts = calculateOperationCounts();

  const handleCardClick = (operationType) => {
    setSelectedOperation(operationType);
  };

  return (
    <div className="metrics-overview">
      <div className="metrics-grid">
        <div 
          className="metric-card read clickable"
          onClick={() => handleCardClick('read')}
        >
          <div className="metric-icon">üìñ</div>
          <div className="metric-content">
            <div className="metric-label">Read Operations</div>
            <div className="metric-value">{operationCounts.actualReadCount}</div>
            <div className="metric-description">Current time window</div>
          </div>
        </div>

        <div 
          className="metric-card write clickable"
          onClick={() => handleCardClick('write')}
        >
          <div className="metric-icon">‚úçÔ∏è</div>
          <div className="metric-content">
            <div className="metric-label">Write Operations</div>
            <div className="metric-value">{operationCounts.actualWriteCount}</div>
            <div className="metric-description">Current time window</div>
          </div>
        </div>

        <div 
          className="metric-card create clickable"
          onClick={() => handleCardClick('create')}
        >
          <div className="metric-icon">üÜï</div>
          <div className="metric-content">
            <div className="metric-label">Create Operations</div>
            <div className="metric-value">{operationCounts.actualCreateCount}</div>
            <div className="metric-description">Current time window</div>
          </div>
        </div>

        <div 
          className="metric-card delete clickable"
          onClick={() => handleCardClick('delete')}
        >
          <div className="metric-icon">üóëÔ∏è</div>
          <div className="metric-content">
            <div className="metric-label">Delete Operations</div>
            <div className="metric-value">{operationCounts.actualDeleteCount}</div>
            <div className="metric-description">Current time window</div>
          </div>
        </div>

        <div 
          className="metric-card modify clickable"
          onClick={() => handleCardClick('modify')}
        >
          <div className="metric-icon">üîÑ</div>
          <div className="metric-content">
            <div className="metric-label">Modifications</div>
            <div className="metric-value">{operationCounts.actualModifyCount}</div>
            <div className="metric-description">Current time window</div>
          </div>
        </div>

        <div 
          className="metric-card total clickable"
          onClick={() => handleCardClick('all')}
        >
          <div className="metric-icon">üìä</div>
          <div className="metric-content">
            <div className="metric-label">Total Events</div>
            <div className="metric-value">{total_events || 0}</div>
            <div className="metric-description">All I/O operations</div>
          </div>
        </div>
      </div>

      {highLoadPaths && highLoadPaths.length > 0 && (
        <div className="high-load-alert">
          <div className="alert-header">
            <span className="alert-icon">‚ö†Ô∏è</span>
            <span className="alert-title">High-Load Paths Detected</span>
          </div>
          <div className="alert-paths">
            {highLoadPaths.slice(0, 5).map((item, idx) => (
              <div key={idx} className="alert-path-item">
                <span className="path-name">{item.path}</span>
                <div className="path-badges">
                  {item.stats.is_high_load && (
                    <span className="badge high-load">High Load</span>
                  )}
                  {item.stats.is_burst && (
                    <span className="badge burst">Burst</span>
                  )}
                </div>
              </div>
            ))}
            {highLoadPaths.length > 5 && (
              <div className="alert-more">
                +{highLoadPaths.length - 5} more paths
              </div>
            )}
          </div>
        </div>
      )}
      {selectedOperation && (
        <EventLogs 
          operationType={selectedOperation}
          onClose={() => setSelectedOperation(null)}
        />
      )}
    </div>
  );
}

export default MetricsOverview;
